import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from "@angular/common/http";

import {Observable} from "rxjs";
import {Utente} from "../model/utente";
import {UtenteForm} from "../model/utente-form";
import {UtenteCard} from "../model/utente-card";
import {UtenteUpdateForm} from "../model/utente-update-form";
import {MyBooks} from "../model/my-books";
import {BookRanking} from "../model/book-ranking";

@Injectable({
  providedIn: 'root'
})
export class UtenteService {

  URL = 'http://localhost:8080/utenti';

  constructor(private http: HttpClient) {
  }

  public findAll(): Promise<UtenteCard[]> {
    return this.http.get<UtenteCard[]>(this.URL, {withCredentials: true}).toPromise();
  }

  public addNewUser(utenteForm: UtenteForm): Observable<object>{
    return this.http.post<object>(this.URL, utenteForm, {withCredentials: true});
  }

  public getSingleUserById(id: number): Promise<Utente> {
    return this.http.get<Utente>(this.URL + "/" + id, {withCredentials: true}).toPromise();
  }

  public updateUser(utente: UtenteUpdateForm, id: number): Observable<object> {
    return this.http.put<object>(this.URL + "/" + id, utente, {withCredentials: true});
  }

  public deleteUser(id: number): Promise<void> {
    return this.http.delete<void>(this.URL + "/" + id, {withCredentials: true}).toPromise();
  }

  public uploadProfilePhoto(profilePhoto: any, id: number): Promise<any> {
    const uploadData = new FormData();
    uploadData.append('fotoProfilo', profilePhoto, profilePhoto.name)
    return this.http.post(this.URL + "/" + id + '/foto_profilo', uploadData, {withCredentials: true}).toPromise();
  }
}

import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {AbstractControl} from "@angular/forms";

@Injectable({
  providedIn: 'root'
})
export class UserValidatorService {

  private timeout;
  private URL = "http://localhost:8080/utenti";

  constructor(private readonly http: HttpClient) { }

  validateUsername(control: AbstractControl): Promise<{[key: string]: boolean}> {
    clearTimeout(this.timeout);
    const value = control.value;

    //Non effettuo chiamate al server quando l'input è vuoto o più corto di due caratteri
    if(!value || value.length < 2) {
      return Promise.resolve(null);
    }

    return new Promise((resolve, reject) => {
      this.timeout = setTimeout(() => {
        let finalUrl: string = this.URL + "/isUsernameUnique";
        this.http.post<boolean>(finalUrl, control.value).subscribe(flag => {
          if(!flag) {
            resolve({'usernameTaken': true});
          }
          else {
            resolve(null);
          }
        },
          (err) => { console.log(err) });
      }, 500)
    })
  }

  validateEmail(control: AbstractControl): Promise<{[key: string]: boolean}> {
    clearTimeout(this.timeout);
    const value = control.value;

    //Non effettuo chiamate al server quando l'input è vuoto o più corto di due caratteri
    if(!value || value.length < 2) {
      return Promise.resolve(null);
    }

    return new Promise((resolve, reject) => {
      this.timeout = setTimeout(() => {
        let finalUrl: string = this.URL + "/isEmailUnique";
        this.http.post<boolean>(finalUrl, control.value).subscribe(flag => {
            if(!flag) {
              resolve({'emailTaken': true});
            }
            else {
              resolve(null);
            }
          },
          (err) => { console.log(err) });
      }, 500)
    })
  }
}

export class UtenteForm {
  nome: string;
  cognome: string;
  username: string;
  password: string;
  email: string;
  descrizione: string;

  constructor(nome: string, cognome: string, username: string, password: string, email: string, descrizione: string) {
    this.nome = nome;
    this.cognome = cognome;
    this.username = username;
    this.password = password;
    this.email = email;
    this.descrizione = descrizione;
  }
}

export class LoggedInfo {
  private _id: number;
  private _admin: boolean;

  constructor() {
    this.id = null;
    this.admin = false;
  }

  get id(): number {
    return this._id;
  }

  set id(value: number) {
    this._id = value;
  }

  get admin(): boolean {
    return this._admin;
  }

  set admin(value: boolean) {
    this._admin = value;
  }
}

import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {LoggedInfo} from "../model/logged-info";

@Injectable({
  providedIn: 'root'
})
export class AuthenticationService {
  logged: boolean = false;
  loggedUser: LoggedInfo = new LoggedInfo();

  private URL = 'http://localhost:8080';
  constructor(private http: HttpClient) { }

  public async loginUser(username: string, password: string){
    var logSuccess: boolean = false;
    const req = this.http.post<void>(this.URL + "/authenticate", {username, password}, {withCredentials: true, observe: "response"});
    await req.toPromise().then(result => {
      let statusCode = result.status;
      if(statusCode == 200) {
        logSuccess = true;
      }
      else {
        logSuccess = false;
      }
    }).catch(err => {
      let statusCode = err.status;
      if(statusCode == 401) {
        logSuccess = false;
      }
    });
    if (logSuccess) {
      await this.http.get<LoggedInfo>(this.URL + "/utenti/loggedUserInfo", {withCredentials: true}).toPromise().then(data => {
        this.loggedUser = data;})
      this.logged = true;
      console.log(this.loggedUser.admin);

    }
    return logSuccess;
  }

  public async logoutUser() {
    this.resetLogVars();
    var logoutSuccess: boolean = false;
    const r1 = this.http.get<void>(this.URL + "/logoutUser", {withCredentials: true, observe: "response"});
    await r1.toPromise().then(result => {let statusCode = result.status;
      if(statusCode == 200) {
        logoutSuccess = true;
      }
      else {
        logoutSuccess = false;
      }
    })
      .catch(error => {
        alert(error.error.status + ": " + error.error.message)
        logoutSuccess = false;
      });
    return logoutSuccess;
  }

  public sameUserOrAdminAuth(id: number): boolean {
    if(this.logged && (this.loggedUser.id == id || this.loggedUser.admin)) {
      return true;
    }
    else return false;
  }

  public sameUser(id: number): boolean {
    if(this.logged && this.loggedUser.id == id) {
      return true;
    }
    else return false;
  }

  public adminAuth(): boolean {
    if(this.logged && this.loggedUser.admin) {
      return true;
    }
    else return false;
  }

  public resetLogVars() {
    this.logged = false;
    this.loggedUser.id = null;
    this.loggedUser.admin = false;
  }
}

import { Component, OnInit } from '@angular/core';
import {Utente} from "../../core/model/utente";
import {ActivatedRoute, Router} from "@angular/router";
import {UtenteService} from "../../core/services/utente.service";
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {Observable} from "rxjs";
import {UserValidatorService} from "../../core/asyncValidators/user-validator.service";
import {UtenteForm} from "../../core/model/utente-form";
import {AuthenticationService} from "../../core/services/authentication.service";

@Component({
  selector: 'app-utente-form',
  templateUrl: './utente-form.component.html',
  styleUrls: ['./utente-form.component.css']
})
export class UtenteFormComponent implements OnInit {
  submitted: boolean = false;
  utenteForm = new FormGroup( {
    nome: new FormControl('', [Validators.required, Validators.minLength(2), Validators.maxLength(24)]),
    cognome: new FormControl('', [Validators.required, Validators.minLength(2), Validators.maxLength(24)]),
    username: new FormControl('', [Validators.required, Validators.minLength(5), Validators.maxLength(24)], this.userValidatorService.validateUsername.bind(this.userValidatorService)),
    password: new FormControl('', [Validators.required, Validators.minLength(8), Validators.maxLength(50)]),
    email: new FormControl('', [Validators.required, Validators.email], this.userValidatorService.validateEmail.bind(this.userValidatorService)),
    descrizione: new FormControl('', [Validators.maxLength(2048)])
  });

  constructor(private route: ActivatedRoute, private router: Router, private userService: UtenteService, private userValidatorService: UserValidatorService, private authenticationService: AuthenticationService) {
  }

  ngOnInit() {
    if(this.authenticationService.logged) {
      this.router.navigate(['/']);
    }
  }

  async onSubmit() {
    this.submitted = true;
    const newUser = new UtenteForm(this.utenteForm.value.nome, this.utenteForm.value.cognome, this.utenteForm.value.username,this.utenteForm.value.password, this.utenteForm.value.email, this.utenteForm.value.descrizione);
    const r = this.userService.addNewUser(newUser);
    await r.toPromise().then(async (data) => {
      for(const fieldName of Object.keys(data)) {
        const serverErrors = data[fieldName];
        const errors = {};
        for(const serverError of serverErrors) {
          errors[serverError] = true;
        }
        const control = this.utenteForm.get(fieldName);
        control.setErrors(errors);
        control.markAsDirty();
      }
      if(this.utenteForm.valid) {
        this.utenteForm.reset();
        this.submitted = false;
        this.goToLogin();
      }
    })
      .catch(error => {
        let statusCode = error.error.status;
        alert(error.error.status + ": " + error.error.message)
        if(statusCode == 401) {
          this.router.navigate(['/login']);
        }
        else this.router.navigate(['/']);
      });
    this.submitted = false;
  }
  goToLogin() {
    this.router.navigate(['/login']);
  }

  isInvalidAndDirty(field: string) {
    const ctrl = this.utenteForm.get(field);
    return !ctrl.valid && ctrl.dirty;
  }

  hasError(field: string, error: string) {
    const ctrl = this.utenteForm.get(field);
    return ctrl.dirty && ctrl.hasError(error);
  }

}

import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from "@angular/common/http";

import {Observable} from "rxjs";
import {Libro} from "../model/libro";
import {LibroForm} from "../model/libro-form";
import {LibroCard} from "../model/libro-card";
import {Prize} from "../model/prize";
import {OverallRatingsForBook} from "../model/overall-ratings-for-book";
import {PrizeForm} from "../model/prize-form";

@Injectable({
  providedIn: 'root'
})
export class LibroService {
  URL = 'http://localhost:8080/libri';
  constructor(private http: HttpClient) { }

  public findAllBooks(): Promise<LibroCard[]> {
    return this.http.get<LibroCard[]>(this.URL, {withCredentials: true}).toPromise();
  }

  public findAllBooksByGenre(genere: String): Promise<LibroCard[]> {
    return this.http.get<LibroCard[]>(this.URL + '?genere=' + genere, {withCredentials: true}).toPromise();
  }

  public findAllBooksContainingTitolo(titolo: string): Promise<LibroCard[]> {
    return this.http.get<LibroCard[]>(this.URL + '?titolo=' + titolo, {withCredentials: true}).toPromise();
  }

  public newBook(newBook: LibroForm): Observable<object> {
    return this.http.post<object>(this.URL, newBook, {withCredentials: true});
  }

  public findBook(id: number): Promise<Libro> {
    return this.http.get<Libro>(this.URL + "/" + id, {withCredentials: true}).toPromise();
  }

  public updateBook(book: LibroForm, id: number): Observable<object> {
    return this.http.put<object>(this.URL + "/" + id, book, {withCredentials: true});
  }

  public deleteBook(id: number): Promise<void> {
    return this.http.delete<void>(this.URL + "/" + id, {withCredentials: true}).toPromise();
  }

  public uploadCoverPhoto(coverPhoto: any, id: number): Promise<any> {
    const uploadData = new FormData();
    uploadData.append('copertina', coverPhoto, coverPhoto.name)
    return this.http.post(this.URL + "/" + id + '/foto_copertina', uploadData, {withCredentials: true}).toPromise();
  }

  public getBookOverallRating(id: number): Promise<OverallRatingsForBook> {
    return this.http.get<OverallRatingsForBook>(this.URL + "/" + id + "/overallRatings",{withCredentials: true}).toPromise();
  }

  public getBooksInSaga(titoloSaga: string, id: number): Promise<LibroCard[]> {
    return this.http.get<LibroCard[]>(this.URL + "?titoloSaga=" + titoloSaga + "&bookId=" + id, {withCredentials: true}).toPromise();
  }

  public getBooksPrizes(id: number): Promise<Prize[]> {
    return this.http.get<Prize[]>(this.URL + "/" + id + "/prizes", {withCredentials: true}).toPromise();
  }

  public addPrizeToBook(prize: PrizeForm, id: number): Observable<object> {
    return this.http.post<object>(this.URL + "/" + id + "/prizes", prize, {withCredentials: true});
  }

  public deleteBooksPrize(prizeID: number, bookID: number): Promise<void> {
    return this.http.delete<void>(this.URL + "/" + bookID + "/prizes/" + prizeID, {withCredentials: true}).toPromise();
  }

  public getPrizeOfBook(prizeName: string, bookID: number): Promise<Prize> {
    return this.http.get<Prize>(this.URL + "/" + bookID + "/prizes/" + prizeName, {withCredentials: true}).toPromise();
  }

  public findAllBooksByAuthor(author: string): Promise<LibroCard[]> {
    return this.http.get<LibroCard[]>(this.URL + "?author=" + author, {withCredentials: true}).toPromise();
  }
}

