  async ngOnInit() {
    this.recensioni = [];
    if(this.type == "user") {
      await this.reviewService.allReviewsByUser(this.identifier).then(recensioni => {recensioni.forEach(recensione => {this.recensioni.push(recensione);})})
        .catch(error => {
          let statusCode = error.error.status;
